import { SpecifyRadiiValue } from '@specifyapp/specify-design-token-format';
import { NumberRounder } from '../makeNumberRounder.js';

export function getRadiiUpdateValue(radii: SpecifyRadiiValue, roundNumber: NumberRounder) {
  return radii.map(v => ({ unit: v.unit, value: roundNumber(v.value) }));
}
