export type SDTFMessageLocation = {
  type: 'SDTF';
  graphPath: Array<string>;
  valuePath: Array<string>;
  targetMode: string | null;
  localMode: string | null;
};

export type JSONMessageLocation = {
  type: 'JSON';
  jsonPath: Array<string>;
};

export type ZodMessageLocation = {
  type: 'zod';
  path: Array<string | number>;
};

export type MessageLocation = SDTFMessageLocation | JSONMessageLocation | ZodMessageLocation;
