import { SPECIFY_SDK_VERSION } from '../../../constants/index.js';
import { generatePersonalAccessTokenHeader } from '../../index.js';

export function formatRequestHeaders(personalAccessToken: string | undefined) {
  return {
    'Content-Type': 'application/json',
    'x-specify-sdk-version': `v${SPECIFY_SDK_VERSION}`,
    ...generatePersonalAccessTokenHeader(personalAccessToken),
  };
}
