import { z } from 'zod';

export const specifyCurrentUserSchema = z
  .object({
    id: z.string(),
    username: z.string(),
    fullname: z.string(),
    email: z.string(),
    organizations: z.array(
      z
        .object({
          id: z.string(),
          namespace: z.string(),
          domain: z.string().nullable(),
          // customer: z
          //   .object({
          //     id: z.string(),
          //   })
          //   .optional(),
        })
        .strict(),
    ),
  })
  .strict();

export type SpecifyCurrentUser = z.infer<typeof specifyCurrentUserSchema>;
