import { MessageLocation } from '../../common/messageLocation.js';
import { SpecifyParsersEngineErrorKey } from '../../errors/index.js';

export type ParsersEngineInformationMessage = {
  type: 'information';
  content: string;
  location?: MessageLocation;
};
export type ParsersEngineWarningMessage = {
  type: 'warning';
  content: string;
  errorKey: SpecifyParsersEngineErrorKey;
  location?: MessageLocation;
};
export type ParsersEngineErrorMessage = {
  type: 'error';
  content: string;
  errorKey: SpecifyParsersEngineErrorKey;
  location?: MessageLocation;
  originalError?: unknown;
};
export type ParsersEngineMessage =
  | ParsersEngineInformationMessage
  | ParsersEngineWarningMessage
  | ParsersEngineErrorMessage;
