export function createMutationDefinition({ name, schema, }) {
    return {
        name,
        schema,
        parse: schema.parse.bind(schema),
        remapAndPipeWith: (map, pipeWith) => (payload) => {
            const validated = schema.parse(payload);
            const args = map(validated);
            return pipeWith(...args);
        },
    };
}
